# ╔═╡ 0920f546-7a3d-45be-9a08-1acb8cf05692
function get_graph_from_edges(n, edges)
	g = SimpleWeightedGraph(n)

	for (a, b) in edges
		add_edge!(g, a, b, 1)
	end

	g
end

